INSERT INTO tb_movie(title, duration, image_url, synopsis, age_rating) VALUES ('TITANIC', '03:15:00', 'https://br.web.img3.acsta.net/r_1280_720/medias/nmedia/18/89/56/94/20081499.jpg', 'Um artista pobre e uma jovem rica se conhecem e se apaixonam na fatídica viagem inaugural do Titanic em 1912. Embora esteja noiva do arrogante herdeiro de uma siderúrgica, a jovem desafia sua família e amigos em busca do verdadeiro amor.', '12');
INSERT INTO tb_movie(title, duration, image_url, synopsis, age_rating) VALUES ('MENINAS MALVADAS', '01:37:00', 'https://br.web.img3.acsta.net/pictures/210/087/21008705_20130527194056821.jpg', 'A adolescente Cady Heron foi educada na África pelos seus pais cientistas. Quando sua família se muda para o subúrbio, nos Estados Unidos, Cady começa a frequentar a escola pública e recebe uma rápida introdução às leis de popularidade que dividem seus colegas. Sem querer, ela acaba no meio de um grupo de elite de estudantes apelidadas "as poderosas".', '12');
INSERT INTO tb_movie(title, duration, image_url, synopsis, age_rating) VALUES ('ORGULHO E PRECONCEITO', '02:09:00', 'https://br.web.img3.acsta.net/medias/nmedia/18/87/84/14/20028635.jpg', 'Elizabeth Bennet vive com sua mãe, pai e irmãs no campo, na Inglaterra. Por ser uma das filhas mais velhas, ela enfrenta uma crescente pressão de seus pais para se casar. Quando Elizabeth é apresentada ao belo e rico Darcy, faíscas voam. Embora haja uma química óbvia entre os dois, a natureza excessivamente reservada de Darcy ameaça a relação.', 'Livre');
INSERT INTO tb_movie(title, duration, image_url, synopsis, age_rating) VALUES ('CASA COMIGO?', '01:40:00', 'https://br.web.img3.acsta.net/medias/nmedia/18/95/25/31/20392336.jpg', 'Mais um ano se passa e Anna continua solteira. A jovem decide ir atrás do namorado na Irlanda e pedi-lo em casamento no dia 29 de fevereiro. Segundo a tradição irlandesa, o homem é obrigado a aceitar o pedido feito nesse dia.', '12');
INSERT INTO tb_movie(title, duration, image_url, synopsis, age_rating) VALUES ('CARTAS PARA JULIETA', '01:45:00', 'https://br.web.img3.acsta.net/medias/nmedia/18/87/32/63/20028692.jpg', 'Em visita à cidade italiana de Verona com seu noivo ocupado, uma jovem chamada Sophie visita um muro onde os desiludidos deixam cartas para a trágica heroína de Shakespeare, Julieta Capuleto. Ao encontrar uma dessas cartas, de 1957, a jovem decide escrever à autora, Claire. Inspirada pela atitude de Sophie, Claire decide procurar por seu antigo amor.', '10');